</li><div id='mynotes_conddiv_"+e+"'><a href='#' data-view-section='condition_"+e+"' data-async='start/condition/"+e+"''><li class='selectable arrow'><strong class='text bold'>"+document.getElementById("condition_name_from_id"+e).value+"</strong></li></a><li id='me_mynote_"+t+"'><div class='my_note'><p class='text' id='my_note_"+t+"'>"+document.getElementById("newnote"+e).value.replace(/\r?\n/g,"<br />")+"</p><br /><a href='#' class='button small' data-label='Edit' data-icon='pencil' onclick='editNote("+t+")'><span class='icon pencil'></span><abbr>Edit</abbr></a><a href='#' class='button cancel on-right small' data-label='Delete' data-icon='remove' onclick='removeNote("+t+")'><span class='icon remove'></span><abbr>Delete</abbr></a></div></li></div>";document.getElementById("mynotes_ul").innerHTML+=s}Lungo.Router.article("condition_"+e,"cond_my_notes_"+e)}})}function editNote(e){document.getElementById("edit_note_textarea").value=document.getElementById("my_note_"+e).innerHTML.replace(/<br\s*\/?>/mg,"\r\n");document.getElementById("edit_note_id").value=e;Lungo.Router.article("edit_note","edit_note")}function saveEditNote(){document.getElementById("edit_note_textarea").blur();var e="start/editnote";var t=document.getElementById("edit_note_id").value;var n="id="+t+"&note="+encodeURIComponent(document.getElementById("edit_note_textarea").value);loadXMLDoc(e,n,function(){if(ajaxdocthing.readyState==4&&ajaxdocthing.status==200){}});if(document.contains(document.getElementById("cond_note_text_"+t))){document.getElementById("cond_note_text_"+t).innerHTML=document.getElementById("edit_note_textarea").value.replace(/\r?\n/g,"<br />")}document.getElementById("my_note_"+t).innerHTML=document.getElementById("edit_note_textarea").value.replace(/\r?\n/g,"<br />");document.getElementById("edit_note_textarea").value="";document.getElementById("edit_note_id").value="";Lungo.Router.back()}function removeNote(e){var t=confirm("Are you sure that you want to delete this note?");if(t==true){var n="start/removenote";var r="id="+e;loadXMLDoc(n,r,function(){if(ajaxdocthing.readyState==4&&ajaxdocthing.status==200){}});var i=document.getElementById("me_mynote_"+e);i.parentNode.removeChild(i);if(document.contains(document.getElementById("cond_mynote_"+e))){i=document.getElementById("cond_mynote_"+e);i.parentNode.removeChild(i)}}}var width=window.innerWidth>0?window.innerWidth:screen.width;document.cookie="devwidth="+width+"; path=/;";var ajaxdocthing;
